// frontend/src/components/Layout/LeagueSidebar.js
import React from "react";

function LeagueSidebar({ leagues, selectedLeague, onSelectLeague }) {
  return (
    <div className="bg-brand-surface p-3 rounded-lg shadow-md">
      <h2 className="text-lg sm:text-xl font-semibold mb-3 text-brand-text-title px-1">
        Лиги
      </h2>
      <ul className="space-y-2">
        {leagues.map((league) => (
          <li key={league.code || "all-leagues"}>
            <button
              onClick={() => onSelectLeague(league.code)}
              className={`w-full flex flex-col items-center justify-center p-2 rounded-md transition-all duration-150 ease-in-out focus:outline-none focus:ring-1 focus:ring-brand-primary-yellow
                ${
                  selectedLeague === league.code ||
                  (!selectedLeague && league.code === null)
                    ? "bg-brand-primary-red text-white shadow-red-glow-sm transform scale-105" // Активная кнопка красная
                    : "text-brand-text-muted hover:bg-gray-700 hover:text-brand-text-light"
                }`}
              title={league.name}
            >
              <div className="w-12 h-12 sm:w-14 sm:h-14 mb-1 bg-gray-700 rounded flex items-center justify-center overflow-hidden border-2 border-transparent">
                {league.logo ? (
                  <img
                    src={league.logo}
                    alt={league.name}
                    className="w-full h-full object-contain p-1"
                  />
                ) : (
                  <span className="text-base sm:text-lg font-bold">
                    {league.code === null
                      ? "ВСЕ"
                      : league.shortName || league.code}
                  </span>
                )}
              </div>
              <span className="text-xs sm:text-sm text-center truncate w-full mt-1 px-1">
                {league.name}
              </span>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
export default LeagueSidebar;
