// frontend/src/components/MatchItem.js
import React from "react";
import { Link } from "react-router-dom";
// import { formatInTimeZone } from 'date-fns-tz'; // Если используем

// Функция для форматирования даты и времени (можно вынести в utils)
const formatMatchDateTime = (utcDateString, localMatchTimeString) => {
  let dateToFormat = localMatchTimeString || utcDateString;
  if (!dateToFormat) return "Время не указано";
  try {
    const date = new Date(dateToFormat);
    return (
      date.toLocaleString("ru-RU", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }) +
      (localMatchTimeString && !String(utcDateString).endsWith("Z")
        ? ` (Местн.)`
        : ` (UTC)`)
    );
  } catch (e) {
    return String(dateToFormat).substring(0, 16).replace("T", " ");
  }
};

// Функция для определения цвета и текста прогноза (теперь более понятный текст)
const getPredictionDisplay = (prediction) => {
  if (prediction === "H")
    return { text: "Прогноз: П1", colorClasses: "bg-green-600 text-white" }; // Зеленый для П1
  if (prediction === "A")
    return {
      text: "Прогноз: П2",
      colorClasses: "bg-brand-primary-red text-white",
    }; // Красный для П2
  if (prediction === "D")
    return {
      text: "Прогноз: X",
      colorClasses: "bg-brand-primary-yellow text-brand-bg",
    }; // Желтый для ничьи
  return {
    text: `Прогноз: ${prediction}`,
    colorClasses: "bg-gray-600 text-white",
  }; // Дефолт
};

function MatchItem({ match }) {
  const predictionDisplay = match.Predicted_Outcome
    ? getPredictionDisplay(match.Predicted_Outcome)
    : null;

  return (
    // Обертка карточки матча, сделанная ссылкой
    <Link
      to={`/match/${match.id}`}
      // Классы для всей карточки: фон, скругление, тень, эффект наведения
      className="block bg-card-bg-gradient rounded-lg shadow-xl border border-brand-card-border overflow-hidden 
                 transform hover:-translate-y-1 hover:shadow-card-hover-glow transition-all duration-300 ease-in-out"
    >
      <div className="p-4 md:p-5 flex flex-col h-full relative">
        {" "}
        {/* padding, flex-колонка, h-full, relative для полоски */}
        {/* Красная вертикальная полоска слева */}
        <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-brand-primary-red to-transparent opacity-75"></div>
        {/* Контент карточки с отступом от левой полоски */}
        <div className="pl-3">
          {/* Логотипы и VS */}
          <div className="flex justify-around items-center mb-3">
            <div className="flex flex-col items-center text-center w-2/5">
              {match.homeTeam_crest && (
                <img
                  src={match.homeTeam_crest}
                  alt={match.homeTeam_name}
                  className="h-16 w-16 object-contain mb-1"
                />
              )}
              <span className="text-sm font-semibold text-brand-text-light truncate w-full">
                {match.homeTeam_name}
              </span>
            </div>
            <span className="text-2xl font-bold text-brand-text-muted">VS</span>
            <div className="flex flex-col items-center text-center w-2/5">
              {match.awayTeam_crest && (
                <img
                  src={match.awayTeam_crest}
                  alt={match.awayTeam_name}
                  className="h-16 w-16 object-contain mb-1"
                />
              )}
              <span className="text-sm font-semibold text-brand-text-light truncate w-full">
                {match.awayTeam_name}
              </span>
            </div>
          </div>

          {/* Прогноз */}
          {predictionDisplay && (
            <div className="my-3 text-center">
              <span
                className={`px-4 py-1.5 text-xs font-bold rounded-full shadow-md ${predictionDisplay.colorClasses}`}
              >
                {predictionDisplay.text} {/* Выводим полный текст прогноза */}
              </span>
            </div>
          )}

          {/* Счет, если матч завершен */}
          {match.status === "FINISHED" && (
            <p className="text-center text-lg font-bold my-2 text-brand-primary-yellow">
              {match.score_fullTime_home} - {match.score_fullTime_away}
            </p>
          )}

          {/* Заполнитель высоты, если нет ни прогноза, ни счета */}
          {!predictionDisplay && match.status !== "FINISHED" && (
            <div className="my-3 h-8"></div>
          )}

          {/* Дата и время */}
          <div className="mt-auto text-center text-xs text-brand-text-muted pt-3">
            {formatMatchDateTime(match.utcDate, match.localMatchTime)}
          </div>
        </div>
      </div>
    </Link>
  );
}

export default MatchItem;
