import React, { useState, useEffect } from "react";
// Импортируем React и его хуки:
// `useState`: для управления состоянием данных внутри компонента (например, данные о матче, статус загрузки).
// `useEffect`: для выполнения побочных эффектов, таких как загрузка данных, когда компонент монтируется или когда меняются определенные значения.

import { useParams } from "react-router-dom";
// Импортируем хук `useParams` из 'react-router-dom'.
// Этот хук позволяет извлекать параметры из URL-адреса. Например, из URL `/match/123` он извлечет `123` как `matchId`.

import { getMatchId } from "../api";
// Импортируем функцию `getMatchId` из файла `api.js`.
// Эта функция отвечает за отправку запроса к твоему бэкенду для получения деталей матча по его ID.

function MatchDetailPage() {
  // Определяем функциональный React-компонент `MatchDetailPage`.

  const { matchId } = useParams();
  // Используем `useParams()` для извлечения `matchId` из текущего URL.
  // Например, если URL `/match/5f7b8c9d0e1f2g3h4i5j6k7l`, то `matchId` будет равен `5f7b8c9d0e1f2g3h4i5j6k7l`.

  const [match, setMatch] = useState(null);
  // Определяем состояние `match`. Изначально оно `null` (матч еще не загружен).
  // `setMatch` - это функция, которая будет использоваться для обновления этого состояния.

  const [loading, setLoading] = useState(true);
  // Определяем состояние `loading`. Изначально `true`, так как данные еще загружаются.
  // `setLoading` - функция для изменения статуса загрузки.

  // Эффект для загрузки данных о матче при монтировании компонента или изменении matchId
  useEffect(() => {
    // Этот `useEffect` запускается каждый раз, когда компонент монтируется
    // или когда значение `matchId` (из URL) изменяется.
    const fetchMatch = async () => {
      // Определяем асинхронную функцию `fetchMatch` для получения данных.
      setLoading(true); // Устанавливаем `loading` в `true`, чтобы показать индикатор загрузки.
      // Вызываем функцию getMatchId из api.js, передавая ID матча из URL
      const data = await getMatchId(matchId);
      // Вызываем функцию API для получения данных о матче по его ID. `await` ждет завершения запроса.
      setMatch(data); // Обновляем состояние `match` полученными данными.
      setLoading(false); // Устанавливаем `loading` в `false`, так как загрузка завершена.
    };

    fetchMatch(); // Вызываем функцию для получения данных.
  }, [matchId]); // Зависимость от `matchId`: эффект перезапустится, если `matchId` изменится.

  // Функция для форматирования даты и времени (дублируется из MatchItem, но это нормально для примера)
  const formatDateTime = (utcDateString, localMatchTimeString) => {
    // Эта функция форматирует дату и время для отображения.
    let dateToFormat = localMatchTimeString || utcDateString;
    // Приоритет отдается локальному времени, если оно есть.
    if (!dateToFormat) return "N/A"; // Если нет данных, возвращаем "N/A".
    try {
      const date = new Date(dateToFormat); // Создаем объект Date.
      return (
        date.toLocaleString("ru-RU", {
          // Форматируем дату в русскую локаль.
          day: "2-digit",
          month: "2-digit",
          year: "numeric",
          hour: "2-digit",
          minute: "2-digit",
        }) +
        // Добавляем пометку часового пояса.
        (localMatchTimeString && !String(utcDateString).endsWith("Z")
          ? ` (Местн.)`
          : ` (UTC)`)
      );
    } catch (e) {
      // Обработка ошибок парсинга даты.
      console.error(
        "Ошибка форматирования даты:",
        e,
        "Исходная строка:",
        dateToFormat
      );
      return String(dateToFormat).substring(0, 16).replace("T", " "); // Возвращаем обрезанную строку.
    }
  };

  // Вспомогательная функция для получения текста победителя матча (для завершенных матчей)
  const getWinnerText = (winner) => {
    // Переводит коды победителя (например, "HOME_TEAM") в читабельный текст.
    if (winner === "HOME_TEAM") return "Хозяева";
    if (winner === "AWAY_TEAM") return "Гости";
    if (winner === "DRAW") return "Ничья";
    return "Неизвестно";
  };

  // Классы для позиционирования sticky сайдбаров (должны совпадать с HomePage.js)
  // Эти переменные определяют стили для "прилипающих" сайдбаров, чтобы они оставались на месте при прокрутке.
  const sidebarStickyTop = "top-16"; // Отступ сверху, равный высоте шапки.
  const sidebarMaxHeight = "max-h-[calc(100vh_-_4rem_-_theme(spacing.8))]"; // Максимальная высота сайдбара.

  // Вспомогательная функция для получения текста прогноза и его цвета
  const getPredictionOutcomeDetails = (outcomeCode) => {
    // Преобразует код прогноза ("H", "A", "D") в текст и соответствующий класс цвета.
    if (outcomeCode === "H")
      return { text: "Победа хозяев (П1)", colorClass: "text-green-500" };
    if (outcomeCode === "A")
      return {
        text: "Победа гостей (П2)",
        colorClass: "text-color-primary-red",
      };
    if (outcomeCode === "D")
      return { text: "Ничья (X)", colorClass: "text-color-primary-yellow" };
    return { text: "Неизвестно", colorClass: "text-color-text-second" };
  };
  // Получаем детали рекомендуемого исхода, используя опциональную цепочку для безопасности
  const recommendedOutcomeDetails = match?.Predicted_Outcome
    ? getPredictionOutcomeDetails(match.Predicted_Outcome)
    : null;
  // Используем оператор опциональной цепочки (`?.`) для безопасного доступа к `Predicted_Outcome`.
  // Если `match` или `Predicted_Outcome` равны `null` или `undefined`, `recommendedOutcomeDetails` будет `null`.

  // --- Условия отображения: загрузка или матч не найден ---
  if (loading) {
    // Если `loading` равно `true`, показываем сообщение "Загрузка...".
    return (
      <div className="flex justify-center items-center h-64">
        <p className="text-xl text-color-text-second animate-pulse">
          Загрузка деталей матча...
        </p>
      </div>
    );
  }

  if (!match) {
    // Если `loading` стало `false`, но `match` все еще `null` (т.е. матч не был найден),
    // показываем сообщение "Матч не найден.".
    return (
      <div className="flex justify-center items-center h-64">
        <p className="text-xl text-color-text-second">Матч не найден.</p>
      </div>
    );
  }

  // --- Основная HTML-разметка страницы деталей матча ---
  return (
    // Главный контейнер страницы: flex-контейнер, колонкой на мобильных, строкой на больших экранах.
    <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
      {/* Левая и центральная колонки для основного контента матча */}
      <section className="w-full lg:flex-grow min-w-0">
        {/* Секция для основной информации о матче. Занимает всю ширину или расширяется на больших экранах. */}

        {/* Блок с логотипами, названиями команд и VS */}
        <div className="bg-color-surface rounded-lg shadow-lg p-6 md:p-8 mb-6 text-center">
          {/* Фон, скругленные углы, тень, отступы, центрирование текста. */}
          <div className="flex justify-around items-center">
            {/* Контейнер для командных логотипов и "VS". */}
            <div className="flex flex-col items-center w-1/3">
              {/* Блок для домашней команды. */}
              {match.homeTeam_crest && (
                // Отображаем логотип, если он есть.
                <img
                  src={match.homeTeam_crest}
                  alt={match.homeTeam_name}
                  className="h-24 w-24 md:h-32 md:w-32 object-contain mb-2"
                />
              )}
              <span className="text-lg md:text-xl font-bold text-color-text-white">
                {match.homeTeam_name}
                {/* Название домашней команды. */}
              </span>
            </div>
            <span className="text-4xl md:text-5xl font-bold text-color-primary-red mx-4">
              VS
            </span>
            {/* Разделитель "VS". */}
            <div className="flex flex-col items-center w-1/3">
              {/* Блок для гостевой команды (аналогично домашней). */}
              {match.awayTeam_crest && (
                <img
                  src={match.awayTeam_crest}
                  alt={match.awayTeam_name}
                  className="h-24 w-24 md:h-32 md:w-32 object-contain mb-2"
                />
              )}
              <span className="text-lg md:text-xl font-bold text-color-text-white">
                {match.awayTeam_name}
              </span>
            </div>
          </div>
          {/* Дата, время, лига, статус */}
          <p className="text-sm md:text-base text-color-text-second mt-4">
            {formatDateTime(match.utcDate, match.localMatchTime)} •{" "}
            {match.competition_name} ({match.competition_code}) • {match.status}
            {/* Отображает отформатированную дату/время, название лиги, ее код и статус матча. */}
          </p>
          {match.matchday && (
            // Если указан тур матча, отображаем его.
            <p className="text-sm text-color-text-second">
              Тур: {match.matchday}
            </p>
          )}
        </div>

        {/* Блок с результатами (если матч завершен) */}
        {match.status === "FINISHED" && (
          // Этот блок отображается ТОЛЬКО, если матч завершен.
          <div className="bg-color-surface rounded-lg shadow-lg p-6 md:p-8 mb-6 text-center">
            <h3 className="text-xl font-bold text-color-primary-yellow mb-3">
              Результат матча
            </h3>
            {/* Заголовок блока результатов. */}
            <p className="text-3xl font-extrabold text-color-text-white mb-2">
              {match.score_fullTime_home} - {match.score_fullTime_away}
              {/* Отображает финальный счет. */}
            </p>
            <p className="text-base text-color-text-second">
              Победитель: {getWinnerText(match.score_winner)}
              {/* Отображает победителя матча, используя вспомогательную функцию. */}
            </p>
          </div>
        )}

        {/* Блок с вероятностями прогнозов */}
        {match.Predicted_Outcome &&
          match.Prob_H !== null &&
          match.Prob_D !== null &&
          match.Prob_A !== null && (
            // Этот блок отображается, если есть прогноз и вероятности для всех исходов.
            <div className="bg-color-surface rounded-lg shadow-lg p-6 md:p-8 mb-6 relative overflow-hidden">
              <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-color-primary-red to-transparent opacity-75"></div>
              {/* Декоративная красная полоска слева (аналогично MatchItem). */}
              <div className="pl-3">
                {" "}
                {/* Отступ от левой полоски */}
                {/* Заголовок "Прогноз и Вероятности" - выравнивание по центру */}
                <h3 className="text-xl font-bold text-color-primary-yellow mb-6 text-center">
                  Прогноз и Вероятности
                </h3>
                {/* Единая полоса прогресса */}
                <div className="w-full bg-gray-700 rounded-full h-3 mb-2 flex overflow-hidden shadow-inner">
                  {/* Контейнер для полосы прогресса. */}
                  {/* Секция Хозяева (H) */}
                  <div
                    className="bg-green-500 h-full transition-all duration-500"
                    style={{ width: `${(match.Prob_H * 100).toFixed(2)}%` }}
                  ></div>
                  {/* Полоска для вероятности победы хозяев (зеленая), ширина динамически задается процентом. */}
                  {/* Секция Ничья (D) */}
                  <div
                    className="bg-color-primary-yellow h-full transition-all duration-500"
                    style={{ width: `${(match.Prob_D * 100).toFixed(2)}%` }}
                  ></div>
                  {/* Полоска для вероятности ничьи (желтая). */}
                  {/* Секция Гости (A) */}
                  <div
                    className="bg-color-primary-red h-full transition-all duration-500"
                    style={{ width: `${(match.Prob_A * 100).toFixed(2)}%` }}
                  ></div>
                  {/* Полоска для вероятности победы гостей (красная). */}
                </div>
                {/* Подписи с процентами - выравнивание по центру */}
                <div className="relative w-full h-0">
                  {" "}
                  {/* Контейнер для процентов, `height 0` чтобы не влиял на разметку. */}
                  {/* Процент Хозяева */}
                  <div
                    className="absolute top-0 text-sm font-bold text-green-500"
                    style={{
                      left: `calc(${match.Prob_H * 100}% / 2 - 2.2ch)`,
                    }} /* Центрируем под Хозяева */
                  >
                    {match.Prob_H !== null
                      ? `${(match.Prob_H * 100).toFixed(1)}%`
                      : "N/A"}
                  </div>
                  {/* Абсолютно позиционированные проценты, центрируются над соответствующими секциями полосы. */}
                  {/* Процент Ничья */}
                  <div
                    className="absolute top-0 text-sm font-bold text-color-primary-yellow"
                    style={{
                      left: `calc(${(match.Prob_H * 100).toFixed(1)}% + ${(
                        match.Prob_D * 100
                      ).toFixed(1)}% / 2 - 2.2ch)`,
                    }} /* Центрируем под Ничья */
                  >
                    {match.Prob_D !== null
                      ? `${(match.Prob_D * 100).toFixed(1)}%`
                      : "N/A"}
                  </div>
                  {/* Процент Гости */}
                  <div
                    className="absolute top-0 text-sm font-bold text-color-primary-red"
                    style={{
                      left: `calc(${(match.Prob_H * 100).toFixed(1)}% + ${(
                        match.Prob_D * 100
                      ).toFixed(1)}% + ${(match.Prob_A * 100).toFixed(
                        1
                      )}% / 2 - 2.2ch)`,
                    }} /* Центрируем под Гости */
                  >
                    {match.Prob_A !== null
                      ? `${(match.Prob_A * 100).toFixed(1)}%`
                      : "N/A"}
                  </div>
                </div>
                {/* Рекомендуемый исход - выравнивание по центру, цвет текста изменен */}
                <p className="text-center text-lg font-bold mt-10">
                  <span className="text-color-text-white">
                    Пронзон от нейросети:{" "}
                  </span>
                  <span className={recommendedOutcomeDetails?.colorClass}>
                    {" "}
                    {/* Применяем цвет из getPredictionOutcomeDetails */}
                    {recommendedOutcomeDetails?.text}
                  </span>
                </p>
                {/* Отображает рекомендуемый исход прогноза, используя данные из `recommendedOutcomeDetails`. */}
              </div>
            </div>
          )}

        {/* Заглушка для блока "Последние матчи" - выравнивание по центру */}
        <div className="bg-color-surface rounded-lg shadow-lg p-6 md:p-8 text-center">
          {/* Заголовок "Последние матчи команд" - выравнивание по центру */}
          <h3 className="text-xl font-bold text-color-primary-yellow mb-3 text-center">
            Последние матчи команд
          </h3>
          {/* Текст заглушки - выравнивание по центру */}
          <p className="text-color-text-second text-center">
            Здесь будет информация о последних матчах {match.homeTeam_name} и{" "}
            {match.awayTeam_name}.
          </p>
          <p className="text-sm text-color-text-second mt-2 text-center">
            (Данные пока недоступны.)
          </p>
          {/* Заглушка для будущей функциональности по отображению последних матчей. */}
        </div>
      </section>

      {/* Правый сайдбар для турнирной таблицы (заглушка) - выравнивание по центру */}
      <aside
        className={`w-full lg:w-72 xl:w-80 flex-shrink-0 hidden md:block lg:sticky ${sidebarStickyTop} self-start ${sidebarMaxHeight} overflow-y-auto rounded-lg`}
      >
        {/* Правый сайдбар, который будет содержать турнирную таблицу (пока заглушка).
            Стили `sticky` и `max-height` позволяют ему "прилипать" к верхней части экрана при скролле. */}
        <div className="bg-color-surface p-4 rounded-lg shadow-md min-h-[300px] flex flex-col text-center">
          {" "}
          {/* ДОБАВЛЕНО text-center ЗДЕСЬ */}
          <h3 className="text-xl font-semibold mb-3 text-color-primary-yellow">
            Турнирная таблица
          </h3>
          <p className="text-color-text-second flex-grow flex items-center justify-center">
            Таблица для {match.competition_name || "выбранной лиги"}
          </p>
          {/* Заглушка для турнирной таблицы. */}
        </div>
      </aside>
    </div>
  );
}

export default MatchDetailPage;
