// frontend/src/components/Layout/Header.js
import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
// Логотип-картинка больше не нужен
// import NeuroPredictLogo from '../../assets/NeuroPredictLogo.png';

const leaguesNav = [
  { code: "PL", name: "Premier League" },
  { code: "BL1", name: "Bundesliga" },
  { code: "SA", name: "Serie A" },
  { code: "PD", name: "La Liga" },
  { code: "FL1", name: "Ligue 1" },
];
const mainNavLinks = [
  { to: "/", text: "Главная" },
  { to: "/history", text: "История матчей" },
  { to: "/tables", text: "Турнирные таблицы" },
];

function Header() {
  const [isLeaguesDropdownOpen, setIsLeaguesDropdownOpen] = useState(false);
  const dropdownRef = useRef(null); // Для выпадающего списка "Лиги"
  const location = useLocation();

  // Закрытие дропдауна по клику вне и при смене URL
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsLeaguesDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [dropdownRef]);

  useEffect(() => {
    setIsLeaguesDropdownOpen(false); // Закрывать при любой смене пути
  }, [location.pathname, location.search]);

  // Стили для кнопок навигации
  const navButtonBaseClasses =
    "px-4 py-2 rounded-full text-xs sm:text-sm font-semibold transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-brand-surface";
  // Активная кнопка: желтый фон, темный текст, красное свечение
  const navButtonActiveClasses =
    "bg-brand-primary-yellow text-brand-text-nav-active shadow-red-button-glow";
  // Неактивная кнопка: темно-серый фон, светлый текст, при наведении красное свечение и изменение фона/текста
  const navButtonInactiveClasses =
    "bg-brand-nav-button-bg text-brand-text-nav-inactive hover:bg-brand-nav-button-hover-bg hover:text-brand-text-nav-hover hover:shadow-red-button-glow";

  return (
    <header className="bg-brand-surface shadow-md sticky top-0 z-50 w-full">
      {/* Убираем красную подсветку снизу, если она больше не нужна или будет другой */}
      {/* <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-brand-primary-red to-transparent opacity-60"></div> */}

      <div className="container mx-auto px-4 h-16 flex justify-between items-center">
        {/* Название сайта (слева) - теперь без картинки логотипа */}
        <Link to="/" className="hover:opacity-90 transition-opacity">
          {/* Применяем новый класс для градиентного текста */}
          <span className="text-2xl md:text-3xl font-extrabold text-gradient-red-yellow tracking-tight">
            NeuroPredict
          </span>
        </Link>

        {/* Центральная навигация */}
        <nav className="flex items-center space-x-1 sm:space-x-2 md:space-x-3">
          {mainNavLinks.map((link) => (
            <NavLink
              key={link.to}
              to={link.to}
              className={({ isActive }) =>
                `${navButtonBaseClasses} ${
                  isActive ||
                  (link.to === "/" &&
                    location.pathname === "/" &&
                    !!location.search)
                    ? navButtonActiveClasses
                    : navButtonInactiveClasses
                }`
              }
            >
              {link.text}
            </NavLink>
          ))}

          {/* Выпадающий список "Лиги" */}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setIsLeaguesDropdownOpen((prev) => !prev)}
              // Кнопка "Лиги" теперь тоже может иметь красную подсветку при наведении/активности
              className={`${navButtonBaseClasses} ${
                isLeaguesDropdownOpen
                  ? navButtonActiveClasses
                      .replace(
                        "bg-brand-primary-yellow",
                        "bg-brand-nav-button-hover-bg"
                      )
                      .replace(
                        "text-brand-text-nav-active",
                        "text-brand-text-nav-hover"
                      )
                  : navButtonInactiveClasses
              } flex items-center`} // Немного другие стили для открытой кнопки
            >
              Лиги
              <svg
                className={`w-3 h-3 ml-1.5 transform transition-transform ${
                  isLeaguesDropdownOpen ? "rotate-180" : ""
                }`}
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
            {isLeaguesDropdownOpen && (
              // Выпадающее меню теперь позиционируется относительно кнопки "Лиги"
              <div className="absolute left-0 mt-2 w-56 bg-brand-surface rounded-md shadow-xl z-30 py-1 border border-gray-700">
                {leaguesNav.map((league) => (
                  <NavLink
                    key={league.code}
                    to={`/?league=${league.code}`}
                    className={(
                      { isActive: isLinkActive } // Используем isLinkActive, чтобы не конфликтовать с isActive из родительского NavLink
                    ) =>
                      `block px-4 py-2 text-sm w-full text-left transition-colors
                      ${
                        searchParams.get("league") === league.code // Проверяем параметр URL для активности
                          ? "bg-brand-primary-red text-white"
                          : "text-brand-text-light hover:bg-gray-700 hover:text-brand-primary-yellow"
                      }`
                    }
                  >
                    {league.name}
                  </NavLink>
                ))}
              </div>
            )}
          </div>
        </nav>

        {/* Правый блок для баланса центрирования (оставляем или настраиваем ширину) */}
        <div className="w-0 sm:w-32 md:w-48 lg:w-[18rem] hidden sm:block">
          {" "}
          {/* Увеличил немного ширину для лучшего центрирования названия */}
        </div>
      </div>
    </header>
  );
}

export default Header;
